document.getElementById('disableNot').addEventListener('click', (evt) => {
    if(localStorage.getItem(`customDesign`) === 'true') {
        localStorage.customDesign = false;
   }else if(localStorage.getItem(`customDesign`) === 'false'){
       localStorage.customDesign = true;
   }
  })
if (localStorage.getItem(`customDesign`)) {
    var checked = JSON.parse(localStorage.getItem('customDesign'));
    document.getElementById("disableNot").checked = checked;
}